mapper.select:
select aucimgno, aucimgname, aucno
from AUCTIONIMAGE
where aucno = #{ aucNo }

mapper.update:
	     update auction 
   		set aucreportingcount = (select aucreportingcount from auction where aucno= #{ aucNo }) +1
   		where aucno =#{ aucNo }

update auction 
set aucblindcheck = 1, aucstate = 6
where aucno = #{ aucNo }

update auction 
set aucstate = aucstate+1 
where aucno = #{ aucNo }

update auction
set aucstate = #{ aucState }
where aucno = #{ aucNo }
